mapper.update:
update sys_menu
menuName = #{menuName}, menuUrl = #{menuUrl}, `type` = #{type}, parentCode = #{parentCode}, ifVisible = #{ifVisible}, ifEnabled = #{ifEnabled}, allowEdit = #{allowEdit}, allowDelete = #{allowDelete}, iconClass = #{iconClass}, `sequence` = #{sequence}, modifier = #{modifier}, remark = #{remark},
where id = #{id,jdbcType=INTEGER}

mapper.sql:
SELECT * from sys_menu

	 id = #{id} 

		and menuName LIKE CONCAT(CONCAT('%', #{menuName}),'%') 


		and menuUrl LIKE CONCAT(CONCAT('%', #{menuUrl}),'%')

	 and `type` = #{type} 
	and parentCode = #{parentCode} 
	and ifVisible = #{ifVisible} 
	and allowEdit = #{allowEdit} 
	 and allowDelete = #{allowDelete} 
	and iconClass = #{iconClass} 
	and `sequence` = #{sequence} 
	and modifier = #{modifier} 
	and ifEnabled = 1
