mapper.select:
select sum(case month(sys_log.createTime) when '1' then 1 else 0 end) as 一月份, sum(case month(sys_log.createTime) when '2' then 1 else 0 end) as 二月份, sum(case month(sys_log.createTime) when '3' then 1 else 0 end) as 三月份, sum(case month(sys_log.createTime) when '4' then 1 else 0 end) as 四月份, sum(case month(sys_log.createTime) when '5' then 1 else 0 end) as 五月份, sum(case month(sys_log.createTime) when '6' then 1 else 0 end) as 六月份, sum(case month(sys_log.createTime) when '7' then 1 else 0 end) as 七月份, sum(case month(sys_log.createTime) when '8' then 1 else 0 end) as 八月份, sum(case month(sys_log.createTime) when '9' then 1 else 0 end) as 九月份, sum(case month(sys_log.createTime) when '10' then 1 else 0 end) as 十月份, sum(case month(sys_log.createTime) when '11' then 1 else 0 end) as 十一月份, sum(case month(sys_log.createTime) when '12' then 1 else 0 end) as 十二月份 from sys_log
year(sys_log.createTime) = #{_parameter};
year(sys_log.createTime) = YEAR (curdate());
